<?php	if(isset($_POST['save']) && $_POST['save'] != "") {		plugin_kontakt::setOption("HTML_SHORT_TAG", $_POST['HTML_SHORT_TAG']);		plugin_kontakt::setOption("HTML_POSITION", $_POST['HTML_POSITION']);		plugin_kontakt::setOption("SPAM_PROTECTION_SHOW", $_POST['SPAM_PROTECTION_SHOW']);		plugin_kontakt::setOption("HTML_TEXT", $_POST['content']);		plugin_kontakt::setOption("BUTTON_SEND_NAME", $_POST['BUTTON_SEND_NAME']);		plugin_kontakt::setOption("BUTTON_RESET_SHOW", $_POST['BUTTON_RESET_SHOW']);		plugin_kontakt::setOption("BUTTON_RESET_NAME", $_POST['BUTTON_RESET_NAME']);				wp_redirect("options-general.php?page=plugin_kontakt&site=settings&notify=save");		exit();	}		if(isset($_GET['notify']) && $_GET['notify'] != "") {		switch($_GET['notify']) {			case "save":				$info = __("The settings was saved.", "pk");			break;			default:				$info = "";			break;		}				if(isset($info) && $info != "") {			print "<div class=\"updated below-h2\" id=\"message\"><p>" . $info . "</p></div><br />";		}	}?><div class="form_designer">	<div class="top">		<h2 class="np"><?php print __("Settings", "pk");?></h2>	</div>	<form method="post" action="options-general.php?page=plugin_kontakt&site=settings">		<table class="wp-list-table widefat fixed pages">			<tr>				<td><?php print __("HTML Short-Tags", "pk");?></td>				<td><?php print __("Text Position", "pk");?></td>			</tr>			<tr>				<td>					<select name="HTML_SHORT_TAG">						<option value="1"<?php print (plugin_kontakt::getOption("HTML_SHORT_TAG") == 1 ? " SELECTED" : ""); ?>><?php print __("Yes", "pk");?></option>						<option value="0"<?php print (plugin_kontakt::getOption("HTML_SHORT_TAG") == 0 ? " SELECTED" : ""); ?>><?php print __("No", "pk");?></option>					</select>					<p>						<?php print __("Short HTML tags can be activated and are responsible for a Valid page (If the DOCTYP permits)", "pk");?>					</p>				</td>				<td>					<select name="HTML_POSITION">						<option value="top"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "top" ? " SELECTED" : ""); ?>><?php print __("Top", "pk");?></option>						<option value="bottom"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "bottom" ? " SELECTED" : ""); ?>><?php print __("Bottom", "pk");?></option>						<option value="left"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "left" ? " SELECTED" : ""); ?>><?php print __("Left", "pk");?></option>						<option value="right"<?php print (plugin_kontakt::getOption("HTML_POSITION") == "right" ? " SELECTED" : ""); ?>><?php print __("Right", "pk");?></option>					</select>					<p>						<?php print __("You can position the form to the defined text content.", "pk");?>					</p>				</td>			</tr>			<tr>				<td><?php print __("Spam Protection", "pk");?></td>				<td><?php print __("Text", "pk");?></td>			</tr>			<tr>				<td>					<select name="SPAM_PROTECTION_SHOW">						<option value="1"<?php print (plugin_kontakt::getOption("SPAM_PROTECTION_SHOW") == 1 ? " SELECTED" : ""); ?>><?php print __("Activated", "pk");?></option>						<option value="0"<?php print (plugin_kontakt::getOption("SPAM_PROTECTION_SHOW") == 0 ? " SELECTED" : ""); ?>><?php print __("Deactivated", "pk");?></option>					</select>					<p>						<?php print __("Here you can enable a Captcha for spam protection", "pk");?>.					</p>				</td>				<td rowspan="5">					<div id="poststuff" class="metabox-holder">						<div id="post-body">							<div id="post-body-content">								<?php									global $q_config;									if(isset($q_config)) {										print '<textarea style="width: 100%; height: 50px;" name="content">' . stripslashes(plugin_kontakt::getOption("HTML_TEXT")) . '</textarea>';										print '<p class="description">' . __("You have installed qTranslate. There have an error to crash Editors, i have disabled it for security", "pk") . '</p>';									} else {										print '<div id="' . (user_can_richedit() ? 'postdivrich' : 'postdiv') . '" class="postarea_one">';										wp_editor(stripslashes(plugin_kontakt::getOption("HTML_TEXT")), "content", array(											'wpautop' => false,											'media_buttons' =>  true,											'textarea_name' => 'content',											'textarea_rows' => get_option('default_post_edit_rows', 10),											'tabindex' => '',											'editor_css' => '',											'editor_class' => 'theEditor',											'quicktags' => true										));																				print '</div>';									}								?>							</div>						</div>						<br class="clear" />					</div>					<p>						<?php print __("Here you can define the content of text in the contact form", "pk");?>					</p>				</td>			</tr>			<tr>				<td><?php print __("Send Button", "pk");?></td>			</tr>			<tr>				<td>					<input type="text" name="BUTTON_SEND_NAME" value="<?php print plugin_kontakt::getOption("BUTTON_SEND_NAME"); ?>" />				</td>			</tr>			<tr>				<td><?php print __("Reset Button", "pk");?></td>			</tr>			<tr>				<td>					<select name="BUTTON_RESET_SHOW">						<option value="1"<?php print (plugin_kontakt::getOption("BUTTON_RESET_SHOW") == 1 ? " SELECTED" : ""); ?>><?php print __("Activated", "pk");?></option>						<option value="0"<?php print (plugin_kontakt::getOption("BUTTON_RESET_SHOW") == 0 ? " SELECTED" : ""); ?>><?php print __("Deactivated", "pk");?></option>					</select>					<input type="text" name="BUTTON_RESET_NAME" value="<?php print plugin_kontakt::getOption("BUTTON_RESET_NAME"); ?>" />				</td>			</tr>		</table>		<br />		<div style="float: right">			<input type="submit" value="<?php print __("Save", "pk");?>" class="button-primary" id="submit" name="save">		</div>	</form></div>