<?php	class plugin_kontakt_functions {		function getIP() {			return $_SERVER['REMOTE_ADDR'];		}				function sendMail($email, $subject, $text) {			wp_mail($email, $subject, $text);		}				function check_email_address($email) {			if(preg_match("/^([a-zA-Z0-9])+([a-zA-Z0-9\._-])*@([a-zA-Z0-9_-])+([a-zA-Z0-9\._-]+)*.([a-zA-Z0-9_-])+([a-zA-Z0-9\._-]+)+$/" , $email)) {				return true;			} else {				return false;			}		}	}?>